package net.andrewhatch.aoc2022;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

class SignalFinderTest {
  final SignalFinder finder = new SignalFinder();

  @Test
  void partOneSampleTest() {
    assertThat(finder.findStartOfPacket("mjqjpqmgbljsphdztnvjfqwrcgsmlb")).isEqualTo(7);
    assertThat(finder.findStartOfPacket("bvwbjplbgvbhsrlpgdmjqwftvncz")).isEqualTo(5);
    assertThat(finder.findStartOfPacket("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")).isEqualTo(10);
  }

  @Test
  void partTwoSampleTest() {
    assertThat(finder.findStartOfMessage("mjqjpqmgbljsphdztnvjfqwrcgsmlb")).isEqualTo(19);
    assertThat(finder.findStartOfMessage("bvwbjplbgvbhsrlpgdmjqwftvncz")).isEqualTo(23);
  }

  @Test
  void partOneInputTest() {
    final String input = "";
    assertThat(finder.findStartOfPacket(input)).isEqualTo(1920L);
  }

  @Test
  void partTwoInputTest() {
    final String input = "";
    assertThat(finder.findStartOfMessage(input)).isEqualTo(2334L);
  }
}
